function alertEncriptar(){
    Swal.fire({
        title: 'Texto encriptado!',
        text: 'Encriptaste el texto!',
        icon: 'success',
        confirmButtonText: 'ok',
        showClass: {
          popup: 'animate__animated animate__fadeInDown'
        },
        hideClass: {
          popup: 'animate__animated animate__fadeOutUp'
        }
        
      })
}

